import React from 'react';
import Container from 'react-bootstrap/Container';
import { FaShoppingCart } from "react-icons/fa";

const product = ({productName, prodImage, posX, posY, price}) => { 
    // return(<div style={{objectPosition: `${posX} ${posY}`, position: 'absolute'}}>
    // return(<div style={{objectPosition: '120px 24px', position: 'absolute', left: `${posX}%`, top: `${posY}%`}}>
    return(
    <Container style={{position: 'absolute', left: `${posX}%`, top: `${posY}%`, width: "25%", 
            border:"2px solid black", borderRadius:"20px", padding: "20px"}}>
        <div className="prodName">
            {productName}
        </div><br/>
        <div style={{height: 100}}>
            <img src={prodImage} style={{maxWidth:100, maxHeight:100}}/>
        </div>
        <FaShoppingCart/>&nbsp;&nbsp;&nbsp;{"$" + price}
        <br/><br/>
        <Container style={{height: "25px"}}>
            {"Unidades: "}
            <button style={{padding: "3px", borderRadius: "5px", padding: "5px", textAlign: "center"}}>-</button>
            &nbsp;&nbsp;1&nbsp;&nbsp;
            <button style={{padding: "3px", borderRadius: "5px", padding: "5px", textAlign: "center", heigth: "5px"}}>+</button>
        </Container>
    </Container>) 
}

export default product;
