import React from 'react';
import '../styles.css';
import PageSetup from '../components/pageSetup';
import {useLocation} from 'react-router-dom';

const DataSheet = () => { 
    const {state} = useLocation();
    const { prodName, prodPrice, prodImage, prodDescription } = state;

    return(<PageSetup generalTitle="Raide Energy" pageTitle="Detalles del producto">
        <div style={{textAlign: "left", paddingTop: "20px", paddingLeft: "50px", position: "relative",
                top: "35vh", fontSize: "20px",}}>
            {prodPrice}
            <img src={prodImage} style={{maxWidth:100, maxHeight:100}}/>
            {prodDescription}

            <div style={{position: "relative",
                border:"2px solid black", borderRadius:"20px", padding: "20px", textAlign: "center", height: "auto",
                minWidth: "300px"}}>
            <div className="prodName">
                {productName}
            </div>
            </div>


        </div>
    </PageSetup>) 
}

export default DataSheet;
