import React from 'react';
import '../styles.css';
import PageSetup from '../components/pageSetup';
import {useLocation} from 'react-router-dom';
import Container from 'react-bootstrap/Container';

const DataSheet = () => { 
    const {state} = useLocation();
    const { prodName, prodPrice, prodImage, prodDescription } = state;

    return(<PageSetup generalTitle="Raide Energy" pageTitle={prodName}>
        <div style={{textAlign: "left", paddingTop: "20px", paddingLeft: "50px", position: "relative",
                top: "35vh", fontSize: "20px",}}>
            {prodPrice}
            {/* <div style={{justifyContent: "center", display: "block"}}> */}
                {/* <Container style={{top: "40vh", position: "relative", display: "block"}}></Container> */}
                <Container style={{display: "inline-block", justifyContent: "center", position: "relative", padding: "20px"}}>
                    <span>
                        <img src={prodImage} style={{maxWidth:500, maxHeight:500}}/>
                    {/* <Container style={{width: "100px", position: "relative", display: "block"}}> */}
                        <Container style={{position: "relative", border:"2px solid black", borderRadius:"20px", 
                            padding: "20px", textAlign: "center", height: "auto", minWidth: "300px", display: "inline"}}>
                            {"Hola"}    
                        </Container>
                    </span>
                    {/* </Container> */}
                </Container>
            {/* </div> */}
            {prodDescription}
            <br/><br/>
            <br/>

            {/* <div className="prodName" style={{textAlign: "center"}}>
                {prodName}
            </div> */}


        </div>
    </PageSetup>) 
}

export default DataSheet;
