import React from 'react';
import '../styles.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Product from '../components/product';
import PageSetup from '../components/pageSetup';
import {Container, Row, Col} from 'react-bootstrap';

import anImage from '../image/solar-panels.jpg';

var numberOfProducts = [["Panel Solar de 500W", "500.00"], 
              ["Panel Solar de 380W", "380.00"], 
              ["Generador Eólico 1000W", "200.00"], 
              ["Producto 4", "0.00"], 
              ["Producto 5", "0.00"], 
              ["1", "42"], 
              ["1", "243.00"]];

const products = () => { 
    return(<PageSetup pageTitle="Productos" style={{justifyContent: "center"}}>
            <Container>
                <Row>
                    <Col sm={8}>sm=8</Col>
                    <Col sm={4}>sm=4</Col>
                </Row>
                <Row>
                    <Col sm>sm=true</Col>
                    <Col sm>sm=true</Col>
                    <Col sm>sm=true</Col>
                </Row>
            </Container>
        {/* <Container style={{top: "30vh", position: "relative", display: "inline-flex", justifyContent: "center",  width: "100%", margin: "0 auto"}}> */}
        <Container style={{top: "30vh", position: "relative", display: "block"}}>
            <Row style={{justifyContent: "center", display: "flex"}}>
                {numberOfProducts.map((item,i) => {
                    return (<Col style={{justifyContent: "center", display: "flex"}} sm>
                        <Product productName={item[0]} prodImage={anImage} posX={7 + (i%3)*30} posY={60 + Math.floor(i/3)*30} price={item[1]}/>
                    </Col>)
                })}
            </Row>
        </Container>
    </PageSetup>) 
}

