import React from 'react';
import '../styles.css';
import PageSetup from '../components/pageSetup';
import {useLocation} from 'react-router-dom';
import {Container, Row, Col} from 'react-bootstrap';

const DataSheet = () => { 
    const {state} = useLocation();
    const { prodName, prodPrice, prodImage, prodDescription } = state;

    return(<PageSetup generalTitle="Raide Energy" pageTitle={prodName}>
        <div style={{textAlign: "left", paddingTop: "20px", paddingLeft: "50px", position: "relative",
                top: "35vh", fontSize: "20px"}}>
            {prodPrice}
            <div style={{justifyContent: "center", display: "flex"}}>
                <Row>
                    {/* <Container style={{display: "inline-block", justifyContent: "center", border:"2px solid black", width: "auto"}}>
                        <span> */}
                            <Col style={{justifyContent:"center", paddingTop: "40px"}}>
                                <img src={prodImage} style={{maxWidth:450, maxHeight:450}}/>
                            </Col>
                            <Col>
                                <Container style={{position: "relative", border:"2px solid black", borderRadius:"20px", 
                                    padding: "20px", textAlign: "center", height: "auto", minWidth: "100px", display: "inline-block",
                                    maxWidth: "400px"}}>
                                    {"Hola aaaaaaaaaaaaaa aaaaaaaaaaaaaaa aaaaaaaaaa aaaaaaaaaa aaaaaaaaaa aaaaaaaaaa aaaaaaaaaa aaaaaaaaaa aaaaaaaaaa  aaaaaaaaaa aaaaaaaaaa aaaaaaaaaa aaaaaaaaaa aaaaaaaaaa aaaaaaaaaa aaaaaaaaaa aaaaaaaaaa"}    
                                </Container>
                            </Col>
                        {/* </span>
                    </Container> */}
                </Row>
            </div>
            {prodDescription}
            <br/><br/>
            <br/>

            {/* <div className="prodName" style={{textAlign: "center"}}>
                {prodName}
            </div> */}


        </div>
    </PageSetup>) 
}

export default DataSheet;
