import React, {useState} from 'react';
import '../styles.css';
import PageSetup from '../components/pageSetup';
import {useLocation} from 'react-router-dom';
import {Container, Row, Col} from 'react-bootstrap';

const ShoppingCartView = () => { 
    const {state} = useLocation();
    const { titulo, prodPrice, prodUnits } = state;

    return(<PageSetup generalTitle="Raide Energy" pageTitle="Carrito de Compras">
        <Container style={{top: "45vh", position: "relative", display: "block", border: "2px solid black",
            borderRadius: "15px"}}>
            <Row>
                <Col style={{textAlign: "left"}}>
                    <img src={URL()} style={{maxWidth:100, maxHeight:100}}/>
                </Col>
                <Col style={{textAlign: "left"}}>{titulo}</Col>
                <Col style={{textAlign: "center"}}>{prodPrice}</Col>
                <Col style={{textAlign: "right"}}>{prodUnits}</Col>
            </Row>
        </Container>
    </PageSetup>) 
}

export default ShoppingCartView;
