import React, {useState, useEffect, useCallback} from 'react';
import '../styles.css';
import PageSetup from '../components/pageSetup';
import {useLocation} from 'react-router-dom';
import {Container, Row, Col} from 'react-bootstrap';

const Item = (titulo, prodPrice, prodUnits, imgSrc, prodId) => { 
    return { titulo: titulo, prodPrice: prodPrice, prodUnits: prodUnits, imgSrc: imgSrc, prodId: prodId } 
}

const ShoppingCartView = () => { 
    const {state} = useLocation();
    const { titulo, prodPrice, prodUnits, imgSrc, prodId } = state;

    const [items, setItems] = useState([]);
    // var hola = [];

    useEffect(() => {
        // localStorage.clear();

        console.log("Value of localStorage: " + localStorage.getItem("Items"));

        if(localStorage.getItem("Items") != null) {
            console.log("ProdId: " + prodId);
            console.log("Boolean value: " + JSON.stringify(JSON.parse(localStorage.getItem("Items")).reduce(item => {
                console.log("Item " + item.titulo);
                return (item.prodId == prodId);
            })));
        }

        if((localStorage.getItem("Items") == null)||(!JSON.parse(localStorage.getItem("Items")).reduce(item => (item.prodId == prodId)))) {
            const newItem = Item(titulo, prodPrice, prodUnits, imgSrc, prodId);

            if(localStorage.getItem("Items") != null) {
                localStorage.setItem("Items", JSON.stringify([...JSON.parse(localStorage.getItem("Items")), newItem]));
            } else {
                localStorage.setItem("Items", JSON.stringify([newItem]));
            }
            console.log("Added item");
        }

        console.log("render");
        console.log("Titulo " + titulo);
        console.log("ProdPrice " + prodPrice);
        console.log("prodUnits " + prodUnits);
        console.log("imgSrc " + imgSrc);
        console.log("prodId " + prodId);

        // hola = JSON.parse(localStorage.getItem("Items"));
        
        setItems([JSON.parse(localStorage.getItem("Items"))]);

        // setItems(localStorage.getItem("Items"));
        // setItems(items => [...items, newItem]);

    }, [prodUnits]);

    return(<PageSetup generalTitle="Raide Energy" pageTitle="Carrito de Compras">
        <Container style={{top: "45vh", position: "relative", display: "block", border: "2px solid black",
            borderRadius: "15px"}}>
            {items.map(item => {
                return(
                    <Row>
                        <Col style={{textAlign: "left"}}>
                            <img src={item.imgSrc} style={{maxWidth:100, maxHeight:100}}/>
                        </Col>
                        <Col style={{textAlign: "left", margin: "auto"}}>{item.titulo}</Col>
                        <Col style={{textAlign: "center", margin: "auto"}}>{item.prodPrice}</Col>
                        <Col style={{textAlign: "right", margin: "auto"}}>{item.prodUnits}</Col>
                        {/* <Col>{(localStorage.getItem("Items"))}</Col> */}
                    </Row>);
            })}

        </Container>
    </PageSetup>) 
}

export default ShoppingCartView;
