import Button from 'react-bootstrap/Button';
import { useNavigate } from "react-router-dom";
import { Outlet } from "react-router-dom";
import React, {useState} from 'react';
import '../styles.css';

function NavBar() {
    let navigate = useNavigate(); 
    const routeChange = (onePath) => { 
        navigate(onePath);
    }

    const [isShrunk,setShrunk] = useState(false);

    useEffect(() => {
        const handler = () => {
            setShrunk((isShrunk) => {
                if (
                  !isShrunk &&
                  (document.body.scrollTop > 20 ||
                    document.documentElement.scrollTop > 20)
                ) {
                  return true;
                }
        
                if (
                  isShrunk &&
                  document.body.scrollTop < 4 &&
                  document.documentElement.scrollTop < 4
                ) {
                  return false;
                }
        
                return isShrunk;
            });
        };
    
        window.addEventListener("scroll", handler);
        return () => window.removeEventListener("scroll", handler);
    }, []);

    return(
    <div>
        <div className="pageFormat">
            <div style={{textAlign: "center", fontSize:"40px", fontWeight:"bold", top: "13vh",
                position: "relative"}}>
                {"Raide Energy"}
            </div>
        </div>
        {/* <div style={{textAlign: "center", fontSize:"40px", fontWeight:"bold", top: "13vh", position: "relative",
            position: "fixed"}}>
            {"Raide Energy"}
        </div> */}
        
        <div className="navBar">
            <Button onClick={() => routeChange('/')} variant="dark" style={{border: "1px solid white", margin: "5px"}}>{"Inicio"}</Button>
            <Button onClick={() => routeChange('/productos')} variant="dark" style={{border: "1px solid white", margin: "5px"}}>{"Productos"}</Button>
            <Button onClick={() => routeChange('/info-contacto')} variant="dark" style={{border: "1px solid white", margin: "5px"}}>{"Contacto"}</Button>
        </div>
        <Outlet/>
    </div>) 
    
}

export default NavBar;
