import React from 'react';
import Container from 'react-bootstrap/Container';
import { FaShoppingCart } from "react-icons/fa";
import Button from 'react-bootstrap/Button';
import {useState} from 'react';

const shoppingCart = ({price}) => { 




    return(
        <div>
            <FaShoppingCart/>&nbsp;&nbsp;&nbsp;{"$" + price}
            &nbsp;&nbsp;&nbsp;
            <div style={{display: "inline-block", justifyContent: "center", borderRadius: "5px"}}>
                <Button variant="dark" style={{border: "1px solid white"}}>
                    {"Agregar"}
                </Button>
            </div>
            <br/><br/>
            <Container style={{height: "25px"}}>
                {"Unidades: "}
                <Button variant='primary' style={{padding: "3px", borderRadius: "5px", padding: "5px", textAlign: "center", 
                    display: "inline-block", wrap: "flex", height: "30px"}}>-</Button>
                &nbsp;&nbsp;1&nbsp;&nbsp;
                <Button variant='primary' style={{padding: "3px", borderRadius: "5px", padding: "5px", textAlign: "center", 
                    heigth: "5px", wrap: "flex", height: "30px"}}>+</Button>
                <br/><br/>
            </Container>
        </div>)
}

export default shoppingCart;
