import React from 'react';
import '../styles.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Product from '../components/product';
import PageSetup from '../components/pageSetup';
import {Container, Row, Col} from 'react-bootstrap';

import anImage from '../image/solar-panels.jpg';

var numberOfProducts = [["Panel Solar de 500W", "500.00"], 
              ["Panel Solar de 380W", "380.00"], 
              ["Generador Eólico 1000W", "200.00"], 
              ["Producto 4", "0.00"], 
              ["Producto 5", "0.00"], 
              ["1", "42"], 
              ["1", "243.00"]];

const products = () => { 
    return(<PageSetup pageTitle="Productos">
        <Container>
            {numberOfProducts.map((item,i) => {
                // var element = <>;

                if(((i%3) == 0) && ((numberOfProducts.length - i) > 1)) {
                    return <Row>
                        <Col>
                            <Product productName={item[0]} prodImage={anImage} posX={7 + (i%3)*30} posY={60 + Math.floor(i/3)*60} price={item[1]}/>
                        </Col>
                        <Col>
                            <Product productName={numberOfProducts[i+1][0]} prodImage={anImage} posX={7 + ((i+1)%3)*30} posY={60 + Math.floor((i+1)/3)*60} price={numberOfProducts[i+1][1]}/>
                        </Col>
                        <Col>
                            <Product productName={numberOfProducts[i+2][0]} prodImage={anImage} posX={7 + ((i+2)%3)*30} posY={60 + Math.floor((i+2)/3)*60} price={numberOfProducts[i+2][1]}/>
                        </Col>
                    </Row>;
                }
                else if (((i%3) == 0) && ((numberOfProducts.length - i) < 1)) {
                    return <Row>
                        <Col>
                            <Product productName={item[0]} prodImage={anImage} posX={7 + (i%3)*30} posY={60 + Math.floor(i/3)*60} price={item[1]}/>
                        </Col>
                    </Row>;
                }
                else if (((i%3) == 0) && ((numberOfProducts.length - i) < 2)) {
                    return <Row>
                        <Col>
                            <Product productName={item[0]} prodImage={anImage} posX={7 + (i%3)*30} posY={60 + Math.floor(i/3)*60} price={item[1]}/>
                        </Col>
                        <Col>
                            <Product productName={numberOfProducts[i+1][0]} prodImage={anImage} posX={7 + ((i+1)%3)*30} posY={60 + Math.floor((i+1)/3)*60} price={numberOfProducts[i+1][1]}/>
                        </Col>
                    </Row>;
                }
                else {
                    return <></>;
                }
            }
                // <Product productName={item[0]} prodImage={anImage} posX={7 + (i%3)*30} posY={60 + Math.floor(i/3)*60} price={item[1]}/>
            )}
        </Container>
    </PageSetup>) 
}

