//             {numberOfProducts.map((item,i) => <Product productName={item[0]} prodImage={anImage} posX={7 + (i%3)*30} posY={60 + Math.floor(i/3)*60} price={item[1]}/>)}
//         </Col>
//     </Row>
// </Container>
// </PageSetup>) 

export default products;
