import React from 'react';
import Container from 'react-bootstrap/Container';
// import { FaShoppingCart } from "react-icons/fa";
import Button from 'react-bootstrap/Button';
import { FaShoppingCart } from 'react-icons/fa';
import ShoppingCart from './shoppingCart';

const product = ({productName, prodImage, posX, posY, price}) => { 
    return(
    <Container style={{position: 'absolute', left: `${posX}%`, top: `${posY}%`, width: "25%", 
            border:"2px solid black", borderRadius:"20px", padding: "20px", textAlign: "center"}}>
        <div className="prodName">
            {productName}
        </div><br/>
        <div style={{height: 100}}>
            <img src={prodImage} style={{maxWidth:100, maxHeight:100}}/>
        </div>
        <ShoppingCart price={price}/>
        {/* <Button onClick={() => routeChange('/viewDetail-' + productName)} variant="dark" style={{border: "1px solid white", margin: "5px"}}>{"Inicio"}</Button>  */}
    </Container>) 
}

export default product;
