import React, {useState, useEffect} from 'react';
import '../styles.css';
import PageSetup from '../components/pageSetup';
import {useLocation} from 'react-router-dom';
import {Container, Row, Col} from 'react-bootstrap';

const Item = (titulo, prodPrice, prodUnits, imgSrc) => { 
    return { titulo: titulo, prodPrice: prodPrice, prodUnits: prodUnits, imgSrc: imgSrc } 
}

const ShoppingCartView = () => { 
    const {state} = useLocation();
    const { titulo, prodPrice, prodUnits, imgSrc } = state;

    const [items, setItems] = useState([]);

    useEffect(() => {
        localStorage.clear();
        const newItem = Item(titulo, prodPrice, prodUnits, imgSrc);

        if(localStorage.getItem("Items") != null) {
            localStorage.setItem("Items", JSON.stringify([...JSON.parse(localStorage.getItem("Items")), newItem]));
        } else {
            localStorage.setItem("Items", JSON.stringify([newItem]));
        }
        
        setItems(JSON.parse(localStorage.getItem("Items")));
        // setItems(localStorage.getItem("Items"));
        // setItems(items => [...items, newItem]);
      }, [titulo, prodPrice, prodUnits, imgSrc]);

    return(<PageSetup generalTitle="Raide Energy" pageTitle="Carrito de Compras">
        <Container style={{top: "45vh", position: "relative", display: "block", border: "2px solid black",
            borderRadius: "15px"}}>
            {items.map(item => {
                return(
                    <Row>
                        <Col style={{textAlign: "left"}}>
                            <img src={item.imgSrc} style={{maxWidth:100, maxHeight:100}}/>
                        </Col>
                        <Col style={{textAlign: "left", margin: "auto"}}>{item.titulo}</Col>
                        <Col style={{textAlign: "center", margin: "auto"}}>{item.prodPrice}</Col>
                        <Col style={{textAlign: "right", margin: "auto"}}>{item.prodUnits}</Col>
                        {/* <Col>{(localStorage.getItem("Items"))}</Col> */}
                    </Row>);
            })}

        </Container>
    </PageSetup>) 
}

export default ShoppingCartView;
