import React from 'react';
import Container from 'react-bootstrap/Container';
// import { FaShoppingCart } from "react-icons/fa";
import Button from 'react-bootstrap/Button';
import { FaShoppingCart } from 'react-icons/fa';
import ShoppingCart from './shoppingCart';
import { useNavigate } from "react-router-dom";

function Product({productName, prodImage, posX, posY, price}) {
    let navigate = useNavigate(); 
    const routeChange = (onePath) => { 
        navigate(onePath);
    }

    return(
        <Container style={{position: "absolute", left: `${posX}%`, top: `${posY}%`, width: "25%", 
                border:"2px solid black", borderRadius:"20px", padding: "20px", textAlign: "center", height: "auto",
                minWidth: "200px"}}>
            <div className="prodName">
                {productName}
            </div><br/>
            <div style={{height: 100}}>
                <img src={prodImage} style={{maxWidth:100, maxHeight:100}}/>
            </div>
            <ShoppingCart price={price}/>
            <Button onClick={() => routeChange('/viewDetail-' + productName)} variant="secondary" style={{border: "1px solid white", margin: "5px", position: "relative", top: "5vh", left: "6vw"}}>
                {"Ver detalles"}
            </Button>
            <br/><br/>
        </Container>) 
} 

